import React, {useState} from 'react';
import "./login.css"
import $ from 'jquery'
import {variables as API} from "../../containers/Variables";


const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    app.use(
        cors({
            origin: ["http://127.0.0.1:8080", "http://127.0.0.1:5500"],
            credentials: true,
        })
    );
    const onSubmitHandler = async (e) => {
        e.preventDefault();
        const response = await fetch(API.LOGIN, {
            method: "POST", headers: {"Content-Type": "Application/json"}, body: JSON.stringify({email, password})
        });
        const content = await response.json();
        debugger;
        if (content.Name !== undefined) {
            LoginSuccess(content.Name)
        } else LoginFailed();

    }


    const LoginSuccess = (_name) => {
        let title = $('#success-fail-title')
        title.text("Welcome " + _name + "!")
        title.css("color", "white")
        $('form').fadeOut(500);
        $('.wrapper').addClass('form-success');
        setTimeout(function () {
            window.location.href = "http://localhost:3000/"
        }, 1500)
    }

    const LoginFailed = () => {
        let title = $('#success-fail-title')
        title.text("Invalid credentials!")
        title.css("color", "red")
    }


    return (<div className="login-page">
        <div className="wrapper">
            <div className="container">
                <h1 id="success-fail-title">Login</h1>

                <form className="form" onSubmit={onSubmitHandler}>
                    <input onChange={e => setEmail(e.target.value)} type="email" placeholder="Email"/>
                    <input onChange={e => setPassword(e.target.value)} type="password" placeholder="Password"/>
                    <button type="submit" id="login-button">Login</button>
                </form>
            </div>

            <ul className="bg-bubbles">
                <li/>
                <li/>
                <li/>
                <li/>
                <li/>
                <li/>
                <li/>
                <li/>
                <li/>
                <li/>
            </ul>
        </div>
    </div>)
}
export default Login
