import React, {Component, useState} from "react";
import {variables} from "../Variables";
// import CustomSelect from "./Selects/CustomSelect";
// import ReactSelect from "./Selects/CustomSelect2";
// import ReactSelectTest from "./Selects/CustomSelectTest";
import CustomSelect from "./Selects/IndianaCustomSelect";


export class Exercise extends Component {
    constructor(props) {
        super(props);
        this.state = {
            muscles: [], thisTitle: "NotChanging", Name: "", selectedOptions: [], Id: 0, exercises: [], musclesTrained: []
        }
    }


    refreshList() {
        fetch(variables.EXERCISE_API_URL)
            .then(response => response.json())
            .then(data => this.setState({exercises: data}))

        fetch(variables.MUSCLE_API_URL)
            .then(response => response.json())
            .then(data => this.setState({muscles: data}))
    }


    componentDidMount() {
        this.refreshList();
    }






    onChangeInput(e){
        let list = e.map(z=> z.value)
        console.log(list)
        // this.setState({musclesTrained: ) ;

    }


    addClick() {
        this.setState({
            thisTitle: "Add Exercise", Id: 0, Name: "", musclesTrained: []
        });
    }

    editClick(exercise) {
        this.setState({
            thisTitle: "Edit Exercise", Id: exercise.Id, Name: exercise.Name, musclesTrained: []
        });
    }


    convertOptions(data) {
        let options = [];
        data.map(function (element) {
            let dict = {label: element.Name, value: element.Id}
            return options.push(dict)
        });
        return options;
    }


    render() {
        const {
            muscles, Id, thisTitle, Name, exercises, musclesTrained, selectedOptions
        } = this.state
        return (

            <div>
                <button type="button"
                        className="btn btn-primary m-2 float-end"
                        data-bs-toggle="modal"
                        data-bs-target="#exampleModal"
                        onClick={() => this.addClick()}>
                    Add Muscle
                </button>
                <h3 className="d-flex justify-content-center m-3">Exercise Page</h3>
                <table className="table table-striped">
                    <thead>
                    <tr>
                        <th>
                            ID
                        </th>
                        <th>
                            Name
                        </th>
                        <th>
                            Muscles Trained
                        </th>
                    </tr>
                    </thead>
                    <tbody>
                    {exercises.map(exercise => <tr key={exercise.Id}>
                        <td>{exercise.Id}</td>
                        <td>{exercise.Name}</td>
                        <td>{exercise.MusclesTrained.map(function (muscle) {
                            if (exercise.MusclesTrained.indexOf(muscle) !== exercise.MusclesTrained.length - 1) {
                                return muscle.Name + ", "
                            } else {
                                return muscle.Name
                            }
                        })}</td>
                        <td>
                            <button type='button'
                                    className="btn btn-light mr-1"
                                    data-bs-toggle="modal"
                                    data-bs-target="#exampleModal"
                                    onClick={() => this.editClick(exercise)}>
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                                     className="bi bi-pencil-square" viewBox="0 0 16 16">
                                    <path
                                        d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                                    <path fillRule="evenodd"
                                          d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                                </svg>
                            </button>

                            <button type='button'
                                    className="btn btn-light mr-1">
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                                     onClick={() => this.deleteClick(exercise.Id)}
                                     className="bi bi-trash-fill" viewBox="0 0 16 16">

                                    <path
                                        d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"/>
                                </svg>
                            </button>
                        </td>
                    </tr>)}
                    </tbody>
                </table>

                <div className="modal fade" id="exampleModal" tabIndex="-1" aria-hidden="true">
                    <div className="modal-dialog modal-lg modal-dialog-centered">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{thisTitle}</h5>
                                <button type="button"
                                        className="btn-close"
                                        data-bs-dismiss="modal"
                                        aria-label="Close">
                                </button>
                            </div>
                            <div className="modal-body">
                                <div className="input-group mb-3">
                                    <span className="input-group-text">Name</span>
                                    <input type="text" className="form-control"
                                        // value={Name}
                                           onChange={this.changeMuscleName}/>
                                </div>



                                <div className="input-group mb-3">
                                    <span className="input-group-text">Muscle</span>
                                    <div className="col-md-auto">
                                        <CustomSelect
                                            options={this.convertOptions(muscles)}
                                            selectedOption = {selectedOptions}
                                            maxOptions={5}
                                            className="form-control"
                                            isMulti={true}
                                            defaultValue={[]}
                                            onChange={this.onChangeInput}/>
                                    </div>
                                </div>


                                <div className="col-md-auto">
                                    {Id === 0 ? <button type="button"
                                                        className="btn btn-primary float-start"
                                                        onClick={() => this.createClick()}
                                    >Create</button> : null}

                                    {Id !== 0 ? <button type="button"
                                                        onClick={() => this.updateClick()}
                                                        className="btn btn-primary float-start">Update</button> : null}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>)
    }
}
