import React from "react";
import CreatableSelect from "react-select";
import { components } from "react-select";
import makeAnimated from "react-select/animated";
import {useState} from "react";

const animatedComponents = makeAnimated();

const Menu = props => {
    const optionSelectedLength = props.getValue().length || 0;
    return (
        <components.Menu {...props}>
            {optionSelectedLength < 5 ? (
                props.children
            ) : (
                <div style={{ margin: 15 }}>Max limit achieved</div>
            )}
        </components.Menu>
    );
};

function MuscleMultiSelect(props) {
    // debugger;
    const isValidNewOption = (inputValue, selectValue) =>
        inputValue.length > 0 && selectValue.length < 5;
    const [values, setValues] = useState([props.options[0], props.options[1]])
    // const [values, setValues] = useState(["laba", "sex"])
    const changeHandler = (e) =>{
        console.log(e)
        setValues([e]);
        console.log(values + "suck")
        setTimeout(function(){console.log(values)}, 1000);
        // setValues([...values, e.target.value]);

        props.onChange(e)
    } 
    return (
        <div className="App">
            <CreatableSelect
                components={{ Menu, animatedComponents }}
                isValidNewOption={isValidNewOption}
                isMulti={props.isMulti}
                onChange={changeHandler}
                options={props.options}
                value={values}
            />
        </div>
    );
}


export default MuscleMultiSelect
