import React, {Component} from "react";

export class Home extends Component{
    componentDidMount() {
        const program = new ShaderProgram( document.querySelector( '.particles' ) )
        const positions = [], colors = []

        for ( var i = 0; i < 1000; i ++ ) {
            positions.push(
                -60 + Math.random() * 120,
                -60 + Math.random() * 120,
                -50 + Math.random() * 100
            )
            colors.push( 0, 0.5, 1, 0.5 )
        }

        program.buffers.position = positions
        program.buffers.color = colors
    }

    render(){
        document.getElementById("sex").style.visibility = "hidden";
        return(
            <div>
                <h3 className="d-flex justify-content-center m-3">Home Page</h3>
            </div>
        )
    }
    
    
    
}
